const buyButtons=document.querySelectorAll(".buy"),cart=document.querySelector(".cart"),cartClose=cart.querySelector(".close-button"),cartContinue=cart.querySelector(".continue-button");for(let t of buyButtons)t.addEventListener("click",function(t){t.preventDefault(),cart.classList.add("cart-show")});cartClose.addEventListener("click",function(t){t.preventDefault(),cart.classList.remove("cart-show")}),cartContinue.addEventListener("click",function(t){t.preventDefault(),cart.classList.remove("cart-show")});
